mapper.select:
select id, title, author, viewCount, DATE_FORMAT(createdDate, '%Y-%m-%d') as createdDate from board
and title like concat('%', #{titleSearchCond.title}, '%')
order by id desc limit #{pageDTO.startRow},#{pageDTO.perPage}

mapper.update:
update board
set viewCount = viewCount + 1
where id = #{id}

mapper.select:
select id, title, content, boardPass, author, viewCount, DATE_FORMAT(createdDate, '%Y-%m-%d %H:%i:%s') as createdDate, attachedFile
from board
where id = #{id}

mapper.update:
update board
set title = #{title}, content = #{content}
where id = #{id}
